mapper.select:

		SELECT *
FROM (
    SELECT rid, bid, replytext, replyer, regdate, ROWNUM AS rnum
    FROM (

        SELECT rid, bid, replytext, replyer, regdate
        FROM reply
        WHERE bid = #{bid}
        ORDER BY rid DESC
    )
    WHERE ROWNUM <= #{cri.pageStart} + #{cri.perPageNum}
)
WHERE rnum > #{cri.pageStart}


SELECT COUNT(bid) AS reply_count
FROM reply
WHERE bid = #{bid}


SELECT bid
FROM reply
WHERE rid = #{rid}
